!function ($) {
    $(document).ready(function ($) {
        $(".search-icon").length > 0 && $(".search-icon").on("click", function (e) {
            e.preventDefault(), $(".search-box-wrap").slideToggle()
        }), $("#mobile-trigger").sidr({
            timing: "ease-in-out",
            speed: 500,
            source: "#mob-menu",
            name: "sidr-main"
        }), $("#mobile-trigger-quick").sidr({
            timing: "ease-in-out",
            side: "right",
            speed: 500,
            source: "#mob-menu-quick",
            name: "sidr-quick"
        });
        var $scrollup_object = $("#btn-scrollup");
        $scrollup_object.length > 0 && ($(window).scroll(function () {
            $(this).scrollTop() > 100 ? $scrollup_object.fadeIn() : $scrollup_object.fadeOut()
        }), $scrollup_object.on("click", function () {
            return $("html, body").animate({scrollTop: 0}, 600), !1
        }));

        $('.contact-collapse').click(function (e) {
            $('#quick-contact').stop(true).slideToggle();
        });
        /*
        $(document).click(function (e) {
            if (!$(e.target).closest('.contact-collapse, #quick-contact').length) {
                $('#quick-contact').stop(true).slideUp();
            }
        });
        */

        $('.collapse1').click(function(){
            $('.reveal1').slideToggle();
        });
        $(".collapse2").on("click", function(){
            $(".reveal2").slideToggle();
        });
        $(".collapse3").on("click", function(){
            $(".reveal3").slideToggle();
        });
        $(".collapse4").on("click", function(){
            $(".reveal4").slideToggle();
        });
        $(".collapse5").on("click", function(){
            $(".reveal5").slideToggle();
        });
        $(".collapse6").on("click", function(){
            $(".reveal6").slideToggle();
        });
        $(".collapse7").on("click", function(){
            $(".reveal7").slideToggle();
        });
        $(".collapse8").on("click", function(){
            $(".reveal8").slideToggle();
        });

    })
}(jQuery);

jQuery(function($) {
    function fixDiv() {
        var $cache = $('#main-nav');
        if ($(window).scrollTop() > 100)
            $cache.css({
                'position': 'fixed',
                'top': '0px'
            });
        else
            $cache.css({
                'position': 'relative',
                'top': 'auto'
            });
    }
    $(window).scroll(fixDiv);
    fixDiv();
});